mapper.update:
UPDATE LECTURE 
SET LT_ST_CODE = 'L02'
WHERE LT_NO = #{ltNo}

UPDATE LECTURE 
SET LT_ST_CODE = 'L06'
WHERE LT_NO = #{ltNo}

mapper.select:
SELECT * FROM( SELECT ROWNUM RN, W.* FROM (SELECT L.LT_NO,L.CRE_ID,C.name down_ctgr, C.up_name up_ctgr,U.NAME,L.TTL,L.REQ_DATE,L.LT_ST_CODE,COUNT(*) OVER() length FROM LECTURE L JOIN (select c.* ,c2.name up_name from ctgr c join ctgr c2 on c2.ctgr_id = c.up_ctgr) C ON L.DOWN_CTGR = C.CTGR_ID JOIN USERS U ON L.CRE_ID = U.ID WHERE 1 =1
AND L.LT_ST_CODE IN ('L02','L07')
AND L.LT_ST_CODE = #{ltStCode}
and u.name like '%' || #{name} || '%'
and L.CRE_ID like '%'||#{creId}||'%'
and l.ttl like '%'||#{ttl}||'%'
AND c.up_name = #{upCtgr}
AND c.name = #{downCtgr}
and TO_DATE(#{start}, 'YYYY-MM-DD') <= TRUNC(l.req_Date)
and TO_DATE(#{end}, 'YYYY-MM-DD') >= TRUNC(l.req_Date)
ORDER BY ${orderColumn} ${orderBy}
ORDER BY LT_NO DESC )W)
where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

SELECT * FROM ( SELECT ROWNUM RN, W.* FROM ( SELECT L.LT_NO,L.CRE_ID,U.NAME, c.code_name lt_st_Code,L.TTL,(L.PRC + L.KIT_PRC) PRC,L.REQ_DATE, R.AVGSTAR ,C.DOWN_CTGR, C.UP_CTGR,COUNT(*) OVER() COUNT FROM LECTURE L JOIN (SELECT C.CTGR_ID , C.NAME DOWN_CTGR, U.NAME UP_CTGR FROM CTGR C JOIN CTGR U ON C.UP_CTGR = U.CTGR_ID) C ON L.DOWN_CTGR = C.CTGR_ID LEFT JOIN (SELECT LT_NO, TRUNC(AVG(STAR),1) AVGSTAR FROM REVIEW WHERE RV_CODE != 'RE03' GROUP BY LT_NO) R ON L.LT_NO = R.LT_NO JOIN USERS U ON L.CRE_ID = U.ID JOIN COMM_CODE C ON LT_ST_CODE = C.CODE_ID WHERE L.TTL LIKE '%'||#{ttl}||'%'
AND L.CRE_ID LIKE '%'||#{creId}||'%'
AND NAME LIKE '%'||#{name}||'%'
AND L.UP_CTGR = #{upCtgr}
AND C.DOWN_CTGR = #{downCtgr}
AND TRUNC(AVGSTAR,0) = #{star}
AND TRUNC(#{pastDate}) < TRUNC(REQ_DATE)
AND TRUNC(REQ_DATE) < TRUNC(#{recentDate})
AND LT_ST_CODE IN
#{ltStCodeList}
ORDER BY ${orderColumn} ${orderBy}
ORDER BY LT_NO DESC
) W )
WHERE RN > (#{page}-1) * 10 AND RN <= #{page} * 10
WHERE RN BETWEEN 1 AND #{endrow}
