mapper.select:
SELECT * FROM LT_QNA
WHERE LT_NO = #{ltNo}
AND WRITER = #{writer} 
ORDER BY Q_REG_DATE DESC

mapper.insert:
INSERT INTO LT_QNA VALUES(
NVL((SELECT MAX(QNA_NO) FROM LT_QNA)+1,1),NULL,#{qContent},#{ltNo},NULL,SYSDATE,'Q01',#{writer}
)

mapper.update:
UPDATE LT_QNA
SET Q_CONTENT = #{qContent}
WHERE QNA_NO = #{qnaNo}

mapper.select:
SELECT * FROM (SELECT ROWNUM RN,QNA_NO,WRITER,A_CONTENT,Q_CONTENT,LT_NO,FINDLTCODE(QNA_ST_CODE) QNA_ST_CODE ,COUNT(*) OVER() CNT FROM LT_QNA WHERE LT_NO = #{ltNo}
AND WRITER LIKE '%'||#{writer}||'%'
AND Q_CONTENT LIKE '%'||#{qContent}||'%'
AND QNA_ST_CODE = #{qnaStCode}
ORDER BY QNA_NO DESC)
WHERE RN > (#{page}-1) * 10 AND RN <= #{page} * 10

mapper.update:
UPDATE LT_QNA
SET A_CONTENT = #{aContent}, A_REG_DATE = SYSDATE, QNA_ST_CODE = 'Q02'
WHERE QNA_NO = #{qnaNo}
