mapper.select:
SELECT * FROM PROGRESS
WHERE LSN_NO = #{lsnNo}
AND ID = #{id}

mapper.update:
UPDATE PROGRESS
SET PROG_PCT = #{progPct}
WHERE ID = #{id}
AND LSN_NO = #{lsnNo}

mapper.select:
SELECT NVL(ROUND(AVG(PROG_PCT)),0) CNT FROM (
    SELECT P.*,L.LT_NO FROM PROGRESS P JOIN LESSON L
	ON P.LSN_NO = L.LSN_NO
	WHERE ID = #{id}
	AND LT_NO = #{ltNo}
)
