mapper.select:
SELECT * FROM PROGRESS
WHERE SERIAL_NO = #{serialNo}
AND ID = #{id}

mapper.update:
UPDATE PROGRESS
SET PROG_PCT = #{progPct}
WHERE ID = #{id}
AND SERIAL_NO = #{serialNo}

mapper.select:
SELECT NVL(ROUND(AVG(PROG_PCT)),0) CNT FROM (
    SELECT P.*,L.LT_NO FROM PROGRESS P JOIN LESSON L
	ON P.SERIAL_NO = L.SERIAL_NO
	WHERE ID = #{id}
	AND LT_NO = #{ltNo}
)
