mapper.select:
	 select* from (
    select rownum rn , w.*
    from(
    SELECT R.*,L.CRE_ID, L.TTL,L.LT_ST_CODE,V.CONTENT RECONTENT , v.star, count(*) over() length 
				FROM REPORT R JOIN LECTURE L
				ON R.LT_NO = L.LT_NO
				LEFT JOIN REVIEW V
				ON R.NUM = V.RV_NO
				where 1=1
				AND R.CTGR = #{ctgr}

				and reporter like '%' || #{reporter}  || '%'


				and L.CRE_ID like '%' || #{creid}  || '%'


				and type = #{type}


				and v.CONTENT like '%' || #{recontent}  || '%'


				and R.RP_ST_CODE = #{rpStCode}


				and ttl like '%' || #{ttl}  || '%'


				and star = #{star}



             and TO_DATE(#{start}, 'YYYY-MM-DD') <= trunc (RP_DATE)  




             and TO_DATE(#{end}, 'YYYY-MM-DD')  >= trunc (RP_DATE) 


    order by rp_no desc) w
    )

	    where rn > (#{pageNum}-1) * #{amount} 
	    and rn <= #{pageNum} * #{amount}

select* from ( select rownum rn , w.* from( SELECT R.*,L.CRE_ID, L.TTL,L.LT_ST_CODE,V.CONTENT RECONTENT , v.star, count(*) over() length FROM REPORT R JOIN LECTURE L ON R.LT_NO = L.LT_NO LEFT JOIN REVIEW V ON R.NUM = V.RV_NO where R.CTGR = #{ctgr}
and reporter like '%' || #{reporter} || '%'
and L.CRE_ID like '%' || #{creid} || '%'
and type = #{type}
and v.CONTENT like '%' || #{recontent} || '%'
and R.RP_ST_CODE = #{rpStCode}
and ttl like '%' || #{ttl} || '%'
and star = #{star}
and TO_DATE(#{start}, 'YYYY-MM-DD') <= trunc (RP_DATE)
and TO_DATE(#{end}, 'YYYY-MM-DD') >= trunc (RP_DATE)
order by rp_no desc) w ) where 1=1
