mapper.insert:
INSERT INTO REVIEW
VALUES(
(SELECT NVL(MAX(RV_NO),1) FROM REVIEW)+1,SYSDATE,'RE01',#{content},#{star},#{ltNo},#{writer}
)

mapper.select:
SELECT * FROM REVIEW
WHERE WRITER = #{writer}
AND LT_NO = #{ltNo}

mapper.update:
UPDATE REVIEW
SET CONTENT = #{content},
STAR = #{star}
WHERE RV_NO = #{rvNo}
