mapper.insert:
INSERT INTO REVIEW
VALUES(
(SELECT NVL(MAX(RV_NO),1) FROM REVIEW)+1,SYSDATE,'RE01',#{content},#{star},#{ltNo},#{writer}
)

mapper.select:
SELECT * FROM REVIEW
WHERE WRITER = #{writer}
AND LT_NO = #{ltNo}
AND RV_CODE != 'RE03'

mapper.update:
UPDATE REVIEW
SET RV_CODE = 'RE03'
WHERE RV_NO = #{rvNo}

UPDATE REVIEW
SET CONTENT = #{content},
STAR = #{star}
WHERE RV_NO = #{rvNo}

mapper.select:
SELECT * FROM (SELECT ROWNUM RN,RV_NO WRITER,CONTENT,STAR, COUNT(*) OVER() CNT, FINDLTCODE(RV_CODE) RV_CODE
FROM REVIEW
WHERE LT_NO = #{ltNo} 
AND WRITER LIKE '%'||#{writer}||'%' 
AND CONTENT LIKE '%'||#{content}||'%'

AND STAR = #{star}


AND RV_CODE = #{rvCode}

ORDER BY RV_NO)

WHERE RN > (#{page}-1) * 10
AND RN <= #{page} * 10
