mapper.select:

SELECT S.*,L.TTL,L.THUMB FROM SUGANG S JOIN LECTURE L
ON S.LT_NO = L.LT_NO
WHERE S.LT_NO=#{ltNo} 
AND S.ID = #{id} 
AND S.EXP_DATE>SYSDATE
AND TLSN_ST_CODE='SU01'

mapper.update:
UPDATE SUGANG 
SET TLSN_ST_CODE = 'SU04'
WHERE TLSN_NO = #{tlsnNo}

mapper.select:
		SELECT COUNT(*) CNT FROM SUGANG
        WHERE ID = #{id}
        AND REG_DATE > SYSDATE-7
        AND TLSN_NO= #{tlsnNo}

SELECT FINDLTCODE(R.RF_ST_CODE) RF_ST_CODE, P.* FROM (SELECT S.*,FINDLTCODE(S.SHIP_ST_CODE)SHIP_ST_NAME,L.TTL,L.INTRO,L.THUMB,L.KIT_NAME,L.KIT_INTRO,L.KIT_PRC,L.CRE_ID FROM SUGANG S JOIN LECTURE L ON S.LT_NO = L.LT_NO WHERE ID = #{id}
AND trunc(S.REG_DATE) >= trunc(#{regDateSearch}) AND trunc(S.REG_DATE) <= trunc(#{expDateSearch})
ORDER BY S.REG_DATE DESC) P LEFT OUTER JOIN REFUND R ON R.TLSN_NO = P.TLSN_NO

mapper.update:
UPDATE SUGANG 
SET SHIP_ST_CODE = #{shipStCode}
WHERE TLSN_NO = #{tlsnNo}

mapper.select:
		SELECT W.* FROM (SELECT ROWNUM RN, U.NAME ,S.ID,S.REG_DATE,S.EXP_DATE, FINDLTCODE(S.TLSN_ST_CODE) TLSN_ST_CODE,COUNT(*) OVER() CNT
		FROM SUGANG S JOIN USERS U
		ON S.ID = U.ID
		WHERE S.LT_NO = #{ltNo}

        	AND U.NAME LIKE '%'||#{name}||'%'


	        AND S.ID LIKE '%'||#{id}||'%'


	        AND S.TLSN_ST_CODE = #{tlsnStCode}


        	AND REG_DATE BETWEEN #{startDate} AND #{endDate}


			ORDER BY ${orderColumn} ${orderBy}


			ORDER BY LT_NO ${orderBy}

        ) W

		WHERE RN > (#{page}-1) * 10
		AND RN <= #{page} * 10

SELECT * FROM (SELECT ROWNUM RN,S.TLSN_NO,S.ID,S.PAY, FINDLTCODE(S.SHIP_ST_CODE) SHIP_ST_CODE, S.SHIP_NUM, S.REG_DATE, COUNT(*)OVER() CNT FROM SUGANG S WHERE S.LT_NO = #{ltNo}
AND S.ID LIKE '%'||#{id}||'%'
AND S.SHIP_NUM LIKE '%'||#{shipNum}||'%'
AND S.SHIP_ST_CODE = #{shipStCode}
AND REG_DATE BETWEEN #{startDate} AND #{endDate}
ORDER BY TLSN_NO DESC )
WHERE RN > (#{page}-1) * 10 AND RN <= #{page} * 10

mapper.update:
update sugang set
ship_st_code = #{shipStCode}, ship_num = #{shipNum}
where tlsn_no = #{tlsnNo}
