mapper.select:
SELECT * FROM (
    SELECT ROWNUM RN, U.*, COUNT(*) OVER() CNT FROM USERS_V U
    WHERE AUTH_CODE != 'A01'
    AND (ID LIKE '%'||#{id}||'%'
	    AND NAME LIKE '%'||#{name}||'%'

	    AND AUTH_CODE = 'A03'


	    AND TEL like '%'||#{tel}||'%'


	    AND GENDER = #{gender}


	    AND #{pastDate} < JOIN_DATE


	    AND JOIN_DATE < #{recentDate}


	    AND U_ST_CODE = #{uStCode}


	    	AND U_GRD_CODE IN 

	    		#{uGrdCodeList}



	    	AND CRE_GRD_CODE IN 

	    		#{creGrdCodeList}


		)			    
	)
WHERE RN > (#{page}-1) * 10
AND RN <= #{page} * 10

mapper.update:
UPDATE USERS 
SET POINT = POINT - #{point}
WHERE ID = #{id}

UPDATE USERS
SET CRE_GRD_CODE = 'C01',
AUTH_CODE = 'A03',
TEL = #{tel},
CRE_INTRO = #{creIntro}
WHERE ID = #{id}
